/* eslint-disable no-useless-constructor */
import React, { Component } from "react";
import uniqid from "uniqid";

class Education extends Component {

    constructor(){
        super();

        this.setState = {
            degree: {school : '', major : '', minor : '',startDate : '', endDate:'', id: uniqid()},
            education: [],
        }
    }

    educationChange = () => {
        this.setState({
            degree: {
                school: document.getElementById('schoolInput').value,
                major: document.getElementById('majorInput').value,
                minor: document.getElementById('minorInput').value,
                startDate: document.getElementById('schoolStartInput').value,
                endDate: document.getElementById('schoolEndInput').value,
                id: this.state.degree.id
            }
        })
    }

    submitEducation = (e) => {
        e.preventDefault();
        this.setState({
            education : this.state.education.concat(this.state.degree),
            degree: {
                school: '',
                major: '',
                minor: '',
                startDate: '',
                endDate: '',
                id: uniqid()
            }
        })
    }

    render(){
        return(
            <div className="educationDiv">
                {education.map((degree) => {
                    return <div className="degree" id={degree.id} key={degree.id}>
                                <div className="school">
                                    <h3>{degree.school}</h3>
                                </div>
                                <div className="major">
                                    <p>{degree.major}</p>
                                </div>
                                <div className="minor">
                                    <p>{degree.minor}</p>
                                </div>
                                <div className="degreeStartDate">
                                    <p>{degree.startDate}</p>
                                </div>
                                <div className="degreeEndDate">
                                    <p>{degree.endDate}</p>
                                </div>
                                <button className="editEducation" >Edit Education</button>
                                <button className="deleteEducation">Delete Education</button>
                            </div>
                })}
            </div>

        )
    }
}


export default Education;
